SELECT distinct a.id id, a.battery_code battery_code, a.battery_name battery_name, a.battery_brand battery_brand, a.battery_pn battery_pn, a.battery_parameters battery_parameters, a.mfrs_id mfrs_id, a.battery_status battery_status, a.create_user create_user, a.create_time create_time, a.update_user update_user, a.update_time update_time, d.vehicle_id vehicle_id, b.mfrs_name mfrs_name FROM biz_battery a LEFT JOIN biz_ref_vehicle_battery d ON a.id = d.battery_id AND d.unbind_time is NULL LEFT JOIN biz_manufacturer b ON a.mfrs_id = b.id LEFT JOIN biz_ref_org_vehicle r ON d.vehicle_id = r.vehicle_id AND r.unbind_time IS NULL LEFT JOIN biz_ref_user_vehicle r1 ON d.vehicle_id = r1.vehicle_id and r1.unbind_time is NULL
1=1
and (a.battery_code like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.battery_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.battery_brand like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.battery_pn like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or b.mfrs_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%'))
and a.battery_status = #{batteryStatus,jdbcType=VARCHAR}
and r1.user_id = #{userId,jdbcType=VARCHAR}
and r.org_id = #{orgId,jdbcType=VARCHAR}
and d.vehicle_id IS NOT NULL
and d.vehicle_id IS NULL
order by a.update_time desc,a.create_time desc,a.battery_status limit #{pageBegin} , #{pageSize}

