select a.id id, a.battery_code battery_code, a.battery_name battery_name, a.battery_brand battery_brand, a.battery_pn battery_pn, a.battery_parameters battery_parameters, a.mfrs_id mfrs_id, a.battery_status battery_status, a.create_user create_user, a.create_time create_time, a.update_user update_user, a.update_time update_time, b.mfrs_name mfrs_name from biz_battery a LEFT JOIN biz_ref_vehicle_battery d ON a.id = d.battery_id AND d.unbind_time IS NULL LEFT JOIN biz_manufacturer b ON a.mfrs_id = b.id LEFT JOIN biz_ref_org_vehicle r ON d.vehicle_id = r.vehicle_id AND r.unbind_time IS NULL LEFT JOIN biz_ref_user_vehicle r1 ON d.vehicle_id = r1.vehicle_id AND r.unbind_time IS NULL where a.id = #{id,jdbcType=VARCHAR}
and r1.user_id = #{userId,jdbcType=VARCHAR}
and r.org_id = #{orgId,jdbcType=VARCHAR}
