SELECT distinct a.id id, a.battery_code battery_code, a.battery_name battery_name, a.battery_brand battery_brand, a.battery_pn battery_pn, a.battery_parameters battery_parameters, a.mfrs_id mfrs_id, a.battery_status battery_status, a.create_user create_user, a.create_time create_time, a.update_user update_user, a.update_time update_time, d.vehicle_id vehicle_id, b.mfrs_name mfrs_name FROM biz_battery a LEFT JOIN biz_ref_vehicle_battery d ON a.id = d.battery_id AND d.unbind_time IS NULL LEFT JOIN biz_manufacturer b ON a.mfrs_id = b.id LEFT JOIN biz_ref_vehicle_battery v ON a.id = v.battery_id LEFT JOIN biz_ref_user_vehicle r ON v.vehicle_id = r.vehicle_id LEFT JOIN sys_user s on r.user_id = s.id
1=1
and (a.battery_code like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.battery_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.battery_brand like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.battery_pn like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.mfrs_id like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or b.mfrs_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%'))
and a.battery_status = #{batteryStatus,jdbcType=VARCHAR}
and s.id = #{userId,jdbcType=VARCHAR}
and s.org_id = #{orgId,jdbcType=VARCHAR}
and d.vehicle_id IS NOT NULL
and d.vehicle_id IS NULL
order by a.update_time desc,a.create_time desc,a.battery_status limit #{pageBegin} , #{pageSize}

SELECT count(distinct a.id) FROM biz_battery a LEFT JOIN biz_ref_vehicle_battery d ON a.id = d.battery_id AND d.unbind_time IS NULL LEFT JOIN biz_manufacturer b ON a.mfrs_id = b.id LEFT JOIN biz_ref_vehicle_battery v ON a.id = v.battery_id LEFT JOIN biz_ref_user_vehicle r ON v.vehicle_id = r.vehicle_id LEFT JOIN sys_user s on r.user_id = s.id
1=1
