mapper.select:
SELECT distinct
FROM biz_organization
1=1
and (org_code like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_name like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_introduce like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_address like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_contacts like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_phone like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_business_licences like concat('%',#{keyStr,jdbcType=VARCHAR},'%'))
and org_type = #{orgType,jdbcType=VARCHAR}
and org_status = #{orgStatus,jdbcType=VARCHAR}
order by update_time desc,create_time desc limit #{pageBegin} , #{pageSize}

SELECT count(distinct id) FROM biz_organization
1=1
and (org_code like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_name like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_introduce like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_address like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_contacts like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_phone like concat('%',#{keyStr,jdbcType=VARCHAR},'%') or org_business_licences like concat('%',#{keyStr,jdbcType=VARCHAR},'%'))
and org_type = #{orgType,jdbcType=VARCHAR}
and org_status = #{orgStatus,jdbcType=VARCHAR}
