SELECT distinct a.id id, a.parts_code parts_code, a.parts_name parts_name, a.parts_brand parts_brand, a.parts_pn parts_pn, a.parts_type parts_type, a.parts_parameters parts_parameters, a.mfrs_id mfrs_id, a.parts_status parts_status, a.create_user create_user, a.create_time create_time, a.update_user update_user, a.update_time update_time, b.mfrs_name mfrs_name, v.vehicle_id FROM biz_parts a LEFT JOIN biz_manufacturer b ON a.mfrs_id = b.id LEFT JOIN biz_ref_vehicle_parts v ON a.id = v.parts_id AND v.unbind_time IS NULL LEFT JOIN biz_ref_org_vehicle r ON v.vehicle_id = r.vehicle_id AND r.unbind_time IS NULL LEFT JOIN biz_ref_user_vehicle r1 ON v.vehicle_id = r1.vehicle_id AND r1.unbind_time IS NULL
1=1
AND (a.parts_code LIKE concat('%',#{keyStr,jdbcType=VARCHAR},'%') OR a.parts_name LIKE concat('%',#{keyStr,jdbcType=VARCHAR},'%') OR a.parts_brand LIKE concat('%',#{keyStr,jdbcType=VARCHAR},'%') OR a.parts_pn LIKE concat('%',#{keyStr,jdbcType=VARCHAR},'%') OR a.parts_parameters LIKE concat('%',#{keyStr,jdbcType=VARCHAR},'%') OR a.mfrs_id LIKE concat('%',#{keyStr,jdbcType=VARCHAR},'%') OR b.mfrs_name LIKE concat('%',#{keyStr,jdbcType=VARCHAR},'%'))
AND a.parts_type = #{partsType,jdbcType=VARCHAR}
AND a.parts_status = #{partsStatus,jdbcType=VARCHAR}
and r1.user_id = #{userId,jdbcType=VARCHAR}
and r.org_id = #{orgId,jdbcType=VARCHAR}
AND v.vehicle_id IS NOT NULL
AND v.vehicle_id IS NULL
order by a.update_time desc,a.create_time desc limit #{pageBegin} , #{pageSize}

