mapper.select:
SELECT distinct

FROM
sys_resources

    1=1

        and (res_code like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%')
        or res_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%'))


        and res_type = #{resType,jdbcType=VARCHAR}


        and show_flag = #{showFlg,jdbcType=VARCHAR}


limit #{pageBegin} , #{pageSize}

SELECT count(distinct id) FROM sys_resources
1=1
and (res_code like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or res_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%'))
and res_type = #{resType,jdbcType=VARCHAR}
and show_flag = #{showFlg,jdbcType=VARCHAR}
