SELECT distinct a.id id, a.login_name login_name, a.user_mobile user_mobile, a.user_type user_type, a.user_icon user_icon, a.password password, a.nick_name nick_name, a.user_name user_name, a.user_real_name_auth_flag user_real_name_auth_flag, a.user_pid user_pid, a.user_ic_front user_ic_front, a.user_ic_back user_ic_back, a.user_ic_group user_ic_group, a.user_status user_status, a.create_user create_user, a.create_time create_time, a.update_user update_user, a.update_time update_time, a.org_id org_id, b.org_code org_code, b.org_name org_name FROM sys_user a LEFT JOIN biz_organization b ON a.org_id=b.id
1=1
and (a.login_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.user_mobile like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.nick_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.user_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.user_pid like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or b.org_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or b.org_code like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%'))
and a.user_type = #{userType,jdbcType=VARCHAR}
and a.user_status = #{userStatus,jdbcType=VARCHAR}
and a.id = #{userId,jdbcType=VARCHAR}
and a.org_id = #{orgId,jdbcType=VARCHAR}
limit #{pageBegin} , #{pageSize}

SELECT count(distinct a.id) FROM sys_user a LEFT JOIN biz_organization b ON a.org_id=b.id
1=1
and (a.login_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.user_mobile like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.nick_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.user_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or a.user_pid like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or b.org_name like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%') or b.org_code like concat('%', #{keyStr,jdbcType=VARCHAR} ,'%'))
and a.user_type = #{userType,jdbcType=VARCHAR}
and a.user_status = #{userStatus,jdbcType=VARCHAR}
and a.id = #{userId,jdbcType=VARCHAR}
and a.org_id = #{orgId,jdbcType=VARCHAR}
